// ── n8n Code node ──
// Goal: split `Target Audience`, `Pain Point`, and `Solution` into separate fields

return items.map(item => {
	// 1) Get the model’s text block
	const content = item.json?.choices?.[0]?.message?.content || '';

	// 2) Break it into lines, trim whitespace
	const lines = content.split(/\n+/).map(l => l.trim()).filter(Boolean);

	// 3) Extract the three values
	const output = {
		targetAudience: '',
		painPoint: '',
		solution: '',
	};

	for (const line of lines) {
		const [label, ...rest] = line.split(':');
		const value = rest.join(':').trim();

		if (/^target audience$/i.test(label)) output.targetAudience = value;
		if (/^pain point$/i.test(label))     output.painPoint     = value;
		if (/^solution$/i.test(label))       output.solution      = value;
	}

	// 4) Return an item with just the three fields
	return { json: output };
});

