// ── n8n Code node ──
// Input:  1 item whose JSON looks like { output: { ads: [ {headline, primaryText, cta}, … ] } }
// Output: N items, each with headline, primaryText, cta, fullAd

const ads = items[0].json.output.ads || [];

return ads.map(ad => {
	// Build the combined string in whatever format you like:
	const fullAd = `${ad.headline}\n\n${ad.primaryText}\n\n${ad.cta}`;

	return {
		json: {
			headline:     ad.headline,
			primaryText:  ad.primaryText,
			cta:          ad.cta,
			fullAd        // new field you can map into Airtable
		}
	};
});

// input: 1 item from the parser
const prompts = items[0].json.output.imagePrompts || [];

// ID of the Ad Copy record we fetched earlier
const adCopyId = $('Get a record2').item.json.id;            // "recA..." (Ad Copy)

// If the Product column in Ad Copy is now a linked-record field,
// it will already contain an array of Product IDs:
const productIdsFromAdCopy = $('Get a record2').item.json.Product || []; // e.g. ["recP..."]

return prompts.map(p => ({
  json: {
    imagePrompt: p,
    adCopyIds:   [ adCopyId ],             // ALWAYS an array
    productIds:  productIdsFromAdCopy      // may be [] if you haven't linked products yet
  }
}));
